import React, { useState, useRef, useEffect } from 'react';

const QRScanner = () => {
    const [isScanning, setIsScanning] = useState(false);
    const [scannedData, setScannedData] = useState('');
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const streamRef = useRef(null);
    const scanIntervalRef = useRef(null);

    // Load QR code scanner library
    useEffect(() => {
        // Load jsQR library for QR code detection
        const script = document.createElement('script');
        script.src = 'https://cdnjs.cloudflare.com/ajax/libs/jsqr/1.4.0/jsQR.min.js';
        script.async = true;
        document.head.appendChild(script);

        return () => {
            if (document.head.contains(script)) {
                document.head.removeChild(script);
            }
        };
    }, []);

    const startScanning = async () => {
        setIsLoading(true);
        setError('');
        setScannedData('');

        try {
            // Request camera permission
            const stream = await navigator.mediaDevices.getUserMedia({
                video: { 
                    facingMode: 'environment', // Use back camera if available
                    width: { ideal: 1280 },
                    height: { ideal: 720 }
                }
            });

            streamRef.current = stream;
            
            if (videoRef.current) {
                videoRef.current.srcObject = stream;
                videoRef.current.setAttribute('playsinline', true);
                videoRef.current.play();
                
                setIsScanning(true);
                setIsLoading(false);
                
                // Start scanning for QR codes
                startQRDetection();
            }
        } catch (err) {
            setError('Unable to access camera. Please ensure camera permissions are granted.');
            setIsLoading(false);
            console.error('Error accessing camera:', err);
        }
    };

    const startQRDetection = () => {
        if (!scanIntervalRef.current) {
            scanIntervalRef.current = setInterval(() => {
                captureAndScan();
            }, 100); // Scan every 100ms
        }
    };

    const captureAndScan = () => {
        if (videoRef.current && canvasRef.current && videoRef.current.readyState === 4) {
            const canvas = canvasRef.current;
            const video = videoRef.current;
            const context = canvas.getContext('2d');

            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;

            context.drawImage(video, 0, 0, canvas.width, canvas.height);
            
            const imageData = context.getImageData(0, 0, canvas.width, canvas.height);
            
            // Check if jsQR is loaded
            if (window.jsQR) {
                const code = window.jsQR(imageData.data, imageData.width, imageData.height);
                
                if (code) {
                    setScannedData(code.data);
                    stopScanning();
                }
            }
        }
    };

    const stopScanning = () => {
        // Stop video stream
        if (streamRef.current) {
            streamRef.current.getTracks().forEach(track => track.stop());
            streamRef.current = null;
        }

        // Clear scanning interval
        if (scanIntervalRef.current) {
            clearInterval(scanIntervalRef.current);
            scanIntervalRef.current = null;
        }

        setIsScanning(false);
        setIsLoading(false);
    };

    const resetScanner = () => {
        setScannedData('');
        setError('');
    };

    // Cleanup on component unmount
    useEffect(() => {
        return () => {
            stopScanning();
        };
    }, []);

    return (
        <div className="w-full max-w-md mx-auto p-6 bg-gray-900 rounded-lg border border-gray-700 shadow-lg relative z-20 form-container">
            <div className="text-center mb-6">
                <h2 className="text-2xl font-bold text-white mb-2">QR Code Scanner</h2>
                <p className="text-gray-400 text-sm">Click scan to open camera and detect QR codes</p>
            </div>

            {/* Error Message */}
            {error && (
                <div className="mb-4 p-3 bg-red-900 bg-opacity-50 border border-red-700 rounded text-red-300 text-sm">
                    {error}
                </div>
            )}

            {/* Scanned Data Display */}
            {scannedData && (
                <div className="mb-4 p-4 bg-green-900 bg-opacity-50 border border-green-700 rounded">
                    <h3 className="text-green-300 font-semibold mb-2">Scanned Data:</h3>
                    <div className="bg-gray-800 p-3 rounded text-white text-sm break-all">
                        {scannedData}
                    </div>
                </div>
            )}

            {/* Scanner Interface */}
            {isScanning ? (
                <div className="space-y-4">
                    <div className="relative bg-black rounded-lg overflow-hidden">
                        <video
                            ref={videoRef}
                            className="w-full h-64 object-cover"
                            autoPlay
                            playsInline
                            muted
                        />
                        <canvas ref={canvasRef} className="hidden" />
                        
                        {/* Scanner Overlay */}
                        <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                            <div className="w-48 h-48 border-2 border-blue-500 border-dashed rounded-lg flex items-center justify-center">
                                <div className="w-44 h-44 border-2 border-blue-400 rounded-lg animate-pulse"></div>
                            </div>
                        </div>
                        
                        {/* Scanning indicator */}
                        <div className="absolute top-4 left-4 bg-blue-600 text-white px-3 py-1 rounded-full text-xs font-medium">
                            📱 Scanning... Point camera at QR code
                        </div>
                        
                        {/* Video dimensions debug info */}
                        {videoRef.current && (
                            <div className="absolute bottom-4 left-4 bg-black bg-opacity-50 text-white px-2 py-1 rounded text-xs">
                                {videoRef.current.videoWidth} × {videoRef.current.videoHeight}
                            </div>
                        )}
                    </div>
                    
                    <button
                        onClick={stopScanning}
                        className="w-full py-3 bg-red-600 hover:bg-red-700 text-white rounded-lg font-medium transition-colors"
                    >
                        Stop Scanner
                    </button>
                </div>
            ) : (
                /* Start Button */
                <div className="space-y-4">
                    <button
                        onClick={startScanning}
                        disabled={isLoading}
                        className="w-full py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-gray-600 text-white rounded-lg font-medium transition-colors flex items-center justify-center"
                    >
                        {isLoading ? (
                            <>
                                <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                                Initializing Camera...
                            </>
                        ) : (
                            'Start QR Scanner'
                        )}
                    </button>
                    
                    {scannedData && (
                        <button
                            onClick={resetScanner}
                            className="w-full py-2 bg-gray-700 hover:bg-gray-600 text-white rounded-lg font-medium transition-colors"
                        >
                            Scan Another Code
                        </button>
                    )}
                </div>
            )}

            {/* Instructions */}
            <div className="mt-6 text-xs text-gray-500 text-center">
                <p>• Point your camera at a QR code</p>
                <p>• Make sure the code is well-lit and clearly visible</p>
                <p>• The scanner will automatically detect and decode the QR code</p>
            </div>
        </div>
    );
};

export default QRScanner;
